import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../reduxStore/store';

import { LineChart, Line, XAxis, Legend, ScaleUtils } from 'recharts';


const formatMonthYear = (value: string) => {
  const date = new Date(value);
  return `${date.toLocaleString('default', { month: 'long' })} ${date.getFullYear()}`;
};

const ProductChart: React.FC = () => {
  const { data, error } = useSelector((state: RootState) => state.data);

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!data) {
    return <div>No data available</div>;
  }

  return (
      <div className="chartInnerDiv">
      <LineChart width={900} height={400} data={data.sales}>
        <XAxis dataKey="weekEnding" tickFormatter={formatMonthYear}/>
        <Legend />
        <Line type="monotone" dataKey="retailSales" stroke="#40a8ef" />
        <Line type="monotone" dataKey="wholesaleSales" stroke="#3c4858" />
      </LineChart>
    </div>

  );
};

export default ProductChart;
