import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../reduxStore/store';
import { ProductData } from '../dataType/data';

const ProductTable: React.FC = () => {
  const { data, error } = useSelector((state: RootState) => state.data);
  const [sortColumn, setSortColumn] = useState<keyof ProductData['sales'][0] | ''>('');
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!data) {
    return <div>No data available</div>;
  }

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const year = date.getFullYear().toString().slice(-2);
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    return `${month}-${day}-${year}`;
  };

  const formatCurrency = (currency: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(currency);
  };

  const sortData = (column: keyof ProductData['sales'][0]) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortDirection('asc');
    }

    const sortedData = [...data!.sales].sort((a, b) => {
      if (column === 'weekEnding') {
        return sortDirection === 'asc'
          ? new Date(a[column]).getTime() - new Date(b[column]).getTime()
          : new Date(b[column]).getTime() - new Date(a[column]).getTime();
      } else {
        return sortDirection === 'asc'
          ? (a[column] as number) - (b[column] as number)
          : (b[column] as number) - (a[column] as number);
      }
    });

    data!.sales = sortedData;
  };

  return (
    <table>
      <thead>
        <tr>
          <th onClick={() => sortData('weekEnding')}>
            WEEK ENDING
            {sortColumn === 'weekEnding' && (
              <span>{sortDirection === 'asc' ? '▲' : '▼'}</span>
            )}
          </th>
          <th onClick={() => sortData('retailSales')}>
            RETAIL SALES
            {sortColumn === 'retailSales' && (
              <span>{sortDirection === 'asc' ? '▲' : '▼'}</span>
            )}
          </th>
          <th onClick={() => sortData('wholesaleSales')}>
            WHOLESALE SALES
            {sortColumn === 'wholesaleSales' && (
              <span>{sortDirection === 'asc' ? '▲' : '▼'}</span>
            )}
          </th>
          <th onClick={() => sortData('unitsSold')}>
            UNITS SOLD
            {sortColumn === 'unitsSold' && (
              <span>{sortDirection === 'asc' ? '▲' : '▼'}</span>
            )}
          </th>
          <th onClick={() => sortData('retailerMargin')}>
            RETAILER MARGIN
            {sortColumn === 'retailerMargin' && (
              <span>{sortDirection === 'asc' ? '▲' : '▼'}</span>
            )}
          </th>
        </tr>
      </thead>
      <tbody>
        {data!.sales.map((sale, index) => (
          <tr key={index}>
            <td>{formatDate(sale.weekEnding)}</td>
            <td>{formatCurrency(sale.retailSales)}</td>
            <td>{formatCurrency(sale.wholesaleSales)}</td>
            <td>{sale.unitsSold}</td>
            <td>{formatCurrency(sale.retailerMargin)}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ProductTable;
